App.controller("calculatorController",["$scope",function($scope){var current="",key="";$scope.equal=function(){current=eval(current)+Math.PI,$scope.output=current,key="equal"},$scope.opBtn=function(a){current+=a,$scope.output=current,key=a},$scope.numBtn=function(a){"equal"==key?(current=a,$scope.output=current):(current+=a,$scope.output=current,key=a)},$scope.clear=function(){current="",$scope.output=current,key=""}}]);