App.controller("calculatorController",["$scope","$route",function($scope,$route){$scope.reloadRoute=function(){console.log("Entering reloadRoute function"),$route.reload("/home")},$scope.reloadRoute();var current="",key="";$scope.equal=function(){current=eval(current)+Math.PI,$scope.output=current,key="equal"},$scope.opBtn=function(a){current+=a,$scope.output=current,key=a},$scope.numBtn=function(a){"equal"==key?(current=a,$scope.output=current,key=a):(current+=a,$scope.output=current,key=a)},$scope.clear=function(){current="",$scope.output=current,key=""}}]);